/**
 * customizer.js
 *
 * Theme Customizer enhancements for a better user experience.
 *
 * Contains handlers to make Theme Customizer preview reload changes asynchronously.
 */
(function(c){wp.customize("blogname",function(b){b.bind(function(a){c(".site-title a").text(a)})});wp.customize("blogdescription",function(b){b.bind(function(a){c(".site-description").text(a)})});wp.customize("header_textcolor",function(b){b.bind(function(a){"blank"===a?c(".site-title a, .site-description").css({clip:"rect(1px, 1px, 1px, 1px)",position:"absolute"}):c(".site-title a, .site-description").css({clip:"auto",color:a,position:"relative"})})})})(jQuery);